import React from "react";
import ReactDOM from "react-dom/client";
import { useState } from "react";
import "./App.css";

function App() {
  const [profile, setProfile] = useState({
    name: "Guest",
    location: {
      city: "Unknown",
    },
  });
function change(e) {
  setProfile(
    (p), { ...p, [name]: e} 
  )

}
  return (
    <>
      <h2>
        UserName: {profile.name}, <br />
        CityNames:
        <ul>
          {profile.location.map((l, index) => {
            return (
              <li key={index}>
                city {index}: {l}
              </li>
            );
          })}
        </ul>
      </h2>
      <label htmlFor="">
        <input type="text" name="cityName" onChange={change} />
      </label>
      <label htmlFor="">
        <input type="text" name="cityLocation" onChange={change} />
      </label>
    </>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
